# Latitude referred to the WGS 84 ellipsoid in degrees

class Point:

    def __init__(self, xml):
        self.le = None
        self.ce = None
        self.hae = None
        self.lon = None
        self.lat = None
        self.setle(xml.get('le'))
        self.setce(xml.get('ce'))
        self.sethae(xml.get('hae'))
        self.setlon(xml.get('lon'))
        self.setlat(xml.get('lat'))
    @classmethod
    def Generic(cls, xml):
        cls.le = None
        cls.ce = None
        cls.hae = None
        cls.lon = None
        cls.lat = None
        cls.setle(cls, xml.get('le'))
        cls.setce(cls, xml.get('ce'))
        cls.sethae(cls, xml.get('hae'))
        cls.setlon(cls, xml.get('lon'))
        cls.setlat(cls, xml.get('lat'))
        return cls

    def getce(cls): 
        return cls.ce 

    # ce setter 
    def setce(cls, ce):
        cls.ce=ce 

    # le getter 
    def getle(cls): 
        return cls.le 

    # le setter 
    def setle(cls,le):
        cls.le=le


    # lat getter 
    def getlat(cls):
        return cls.lat 

    # lat setter 
    def setlat(cls, lat):  
        cls.lat=lat

        # lon getter 
    def getlon(cls):
        return cls.lon 

    # lon setter 
    def setlon(cls,lon):
        cls.lon=lon
  
    def gethae(cls):
        return cls.hae

    def sethae(cls,hae):
        cls.hae = hae
